// Get the relevant application properties
var towerAddress = gs.getProperty("x_271208_panomator.towerAddress");
var towerJobTemplate = gs.getProperty("x_271208_panomator.towerRuleJob");
var towerUsername = gs.getProperty("x_271208_panomator.towerUsername");
var towerPassword = gs.getProperty("x_271208_panomator.towerPassword");

// Build the JSON object
var jsonPayload = {};
jsonPayload.extra_vars = {};
jsonPayload.extra_vars.rule_devicegroup = current.u_rule_devicegroup.toString();
jsonPayload.extra_vars.rule_name = current.u_rule_name.toString();
jsonPayload.extra_vars.rule_description = current.description.toString();
jsonPayload.extra_vars.rule_source_zone = current.u_src_zone.toString();
jsonPayload.extra_vars.rule_source_ip = current.u_src_address.toString();
jsonPayload.extra_vars.rule_source_user = current.u_src_user.toString();
jsonPayload.extra_vars.rule_hip_profile = current.u_hip_profile.toString();
jsonPayload.extra_vars.rule_destination_zone = current.u_dst_zone.toString();
jsonPayload.extra_vars.rule_destination_ip = current.u_dst_address.toString();
jsonPayload.extra_vars.rule_application = current.u_application.toString();
jsonPayload.extra_vars.rule_service = current.u_service.toString();
jsonPayload.extra_vars.rule_category = 'any';
jsonPayload.extra_vars.rule_action = current.u_action.toString();
jsonPayload.extra_vars.rule_state = 'present';
jsonPayload.extra_vars.rule_secprofile = 'lab-spg';
jsonPayload.extra_vars.rule_logprofile = 'lab-lfp';
jsonPayload.extra_vars.snow_table = 'lab-lfp';
jsonPayload.extra_vars.rule_logprofile = 'lab-lfp';
jsonPayload.extra_vars.snow_table = 'x_271208_panomator_requests';
jsonPayload.extra_vars.snow_request = current.number.toString();

try{
    var request = new sn_ws.RESTMessageV2();
    request.setLogLevel('all');
	
    // REST message parameters
    request.setEndpoint('https://' + towerAddress + '/api/v2/job_templates/' + towerJobTemplate + '/launch');
	request.setBasicAuth(towerUsername, towerPassword);
    request.setHttpMethod('POST');
    request.setRequestHeader('Content-Type', 'application/json');
    //request.setMIDServer('midsrv1'); // Shouldn't need this if the MID Application default is set.
    request.setEccParameter('skip_sensor', true);

    // Process the request
    request.setRequestBody(global.JSON.stringify(jsonPayload));
    var response = request.execute();
    var httpResponseStatus = response.getStatusCode();
    var httpResponseContentType = response.getHeader('Content-Type');
    var parser = new global.JSONParser();
    var parsed = {};
    var httpResponseBody;
    
    // Dump stats
    gs.debug("http response status_code: " + httpResponseStatus);
    gs.debug("http response content-type: " + httpResponseContentType);

    //  if request is successful then parse the response body
    if (httpResponseStatus == 201 && httpResponseContentType == 'application/json') {
        httpResponseBody = response.getBody();
        //  parse JSON string returned from request into a json object
        parsed = parser.parse(httpResponseBody);
        gs.debug(parsed);
    }
} catch(ex) {
    error = ex.message;
    gs.debug(error);
}

